import React, { useContext, useState } from "react";
import UserContext from "../Context/UserContext";
import { Card } from '../Card/card';
import { Formik, Form } from 'formik'; 
import { TextField } from '../TextField/textfield';
import * as Yup from 'yup';

function CreateAccount() {
    const { user } = useContext(UserContext);

    const [usersList, setUsersList] = useState(user);

    const validate = Yup.object({
        name: Yup.string()
            .required('Required'),

        email: Yup.string().email('Write in email format')
            .required('Email is required'),


        password: Yup.string()
            .min(8, 'Must be at least 8 characters')
            .required('Password is required'),

        confirmPassword: Yup.string()
            .oneOf([Yup.ref('password'), null], "Confirm same Password")
            .required('Confirm password required'),
    });

    const [show, setShow] = useState('true');
    const [name, setName] = useState('');
    const [email,setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');



    function clearForm(){
        setName('');
        setEmail('');
        setPassword('');
        setConfirmPassword('');
        setShow(true);
    }


    return(
        <Card
            txtcolor="black"
            header="Create An Account"
            title="Fill in the following information"
            body={(
                <>
                show ? (
                    <Formik
                        initialValues={{
                            name: "",
                            email: "",
                            password: "",
                            confirmPassword:""
                        }}
                        validationSchema={validate}
                        onSubmit={values => {
                            console.log(values);
                            setUsersList(usersList.push(values));
                            console.log(usersList);
                        }}
                    >
                        { formik => (
                            <div>

                                <Form>
                                    <TextField label="Name" name="name" type="text"  />
                                    <TextField label="Email" name="email" type="email" />
                                    <TextField label="Password" name="password" type="password" />
                                    <TextField label="Confirm Password" name="confirmPassword" type="password"/>

                                    <button className="btn btn-dark mt-5" type="submit" disabled={!formik.isValid || formik.isSubmitting} >Create Account</button>
                                </Form>
                            </div>
                        )

                        }
                    </Formik>
                ) : (
                    <>
                        <Card
                            txtcolor="black"
                            header="You have successfully added an Account!!!"
                            title="Would you like to Add another Account?"
                            body={(
                                <>
                                    <button type="button" className="btn btn-success" onClick={clearForm}>Add Another Account</button>
                                </>
                            )}

                        />
                    </>
                )
                </>
            )}
        />


    );
}


export { CreateAccount };
