import React, { useContext, useState } from "react";
import UserContext from "../Context/UserContext";
import { Card } from '../Card/card';
import { Formik, Form } from 'formik'; 
import { TextField } from '../TextField/textfield';
import * as Yup from 'yup';

import { v4 as uuidv4 } from 'uuid';


function CreateAccount() {
    const { user } = useContext(UserContext);

    const [usersList, setUsersList] = useState(user);
    const [show, setShow] = useState(false);

    const validate = Yup.object({
        name: Yup.string()
            .required('Required'),

        email: Yup.string().email('Write in email format')
            .required('Email is required'),


        password: Yup.string()
            .min(8, 'Must be at least 8 characters')
            .required('Password is required'),

        confirmPassword: Yup.string()
            .oneOf([Yup.ref('password'), null], "Confirm same Password")
            .required('Confirm password required'),
    });


    return(
        <div className="container-fluid">
            <div className="row">
                <div className="col align-self-center">
                    <Card
                        bgcolor="info"
                        header="Create An Account"
                        title="Fill in the following information"
                        body={
                            show ? (
                            <>
                                <Formik
                                    initialValues={{
                                        name: "",
                                        email: "",
                                        password: "",
                                        confirmPassword:""
                                    }}
                                    validationSchema={validate}
                                    onSubmit={(values, {resetForm}) => {
                                        //console.log(values);

                                        setUsersList(usersList.push({
                                            id: uuidv4(),
                                            ...values
                                        }
                                            ));
                                        //console.log(usersList);
                                        resetForm({values: ""});

                                    }}
                                >
                                    { formik => (
                                        <div>

                                            <Form>
                                                <TextField label="Name" name="name" type="text"  />
                                                <TextField label="Email" name="email" type="email" />
                                                <TextField label="Password" name="password" type="password" />
                                                <TextField label="Confirm Password" name="confirmPassword" type="password"/>

                                                <button className="btn btn-secondary mt-5" type="submit" disabled={!formik.isValid || formik.isSubmitting} >Create Account</button>
                                            </Form>
                                        </div>
                                    )

                                    }
                                </Formik>

                            </>
                        ):(
                            <>
                                <div class="card">
                                    <div class="card-body">
                                        <h5 class="card-title">You have added a new Account</h5>
                                        <p class="card-text">Would you like to add another account?</p>
                                        <a href="#" class="btn btn-primary">Yes, please!</a>
                                    </div>
                                </div>
                            </>
                        )}
                    />
                </div>
            </div>
        </div>

    );
}


export { CreateAccount };
