import React, {useContext, useState} from "react";
import UserContext from "../Context/UserContext";
import { Card } from '../Card/card';


function Withdraw(totalsState) {
    const { user } = useContext(UserContext);


    const [withdraw, setWitdraw] = useState(0);
    const [totalState, setTotalState] = useState(100);
    const [validTransaction, setValidTransaction] = useState(false);

    const handleChange = (event) =>{
        if(withdraw < 0) {
            setWitdraw(0)
        } else {
            setWitdraw(Number(event.target.value));
        }
        setValidTransaction(false);
        if(event.target.value <= 0) return;

        if(event.target.value < totalState) {
            setValidTransaction(false);
        } else {
            setValidTransaction(true)
        }
    };

    const handleSubmit = (event) =>{
        let newTotal = totalState - withdraw;
        setTotalState(newTotal);
        setValidTransaction(false);




        event.preventDefault();
    };


    return(
        <Card
            txtcolor="black"
            header="Withdraw"

            body={(
                <>
                    <form onSubmit={handleSubmit}>
                        <div className="row mb-3">
                            <div className="col-auto">
                                <label id="total" className="form-label">BALANCE</label>
                            </div>
                            <div className="col-auto">
                                <label id="total">{totalState}</label>
                            </div>
                        </div>

                        <div className="row mb-3">
                            <label id="total" className="form-label">WITHDRAW AMOUNT</label>
                        </div>
                        <div className="row mb-3">
                            <input id="number-input" type="input" width="200" onChange={handleChange}/>
                        </div>
                        <div className="row mb-3">
                            <button type="submit" width="200" value="Submit" id="submit-input" disabled={validTransaction}>WITHDRAW</button>
                        </div>

                    </form>

                </>
            )}
        />
    )
}

export { Withdraw };
