import React, { useContext, useState } from "react";
import TransactionContext from "../Context/TransactionContext";
import { Card } from '../Card/card';
import { v4 as uuidv4 } from 'uuid';


function Deposit() {
    const { data } =useContext(TransactionContext);

    const [transactionsList, setTransactionsList] = useState(data);
    const [deposit, setDeposit] = useState(0);
    const [totalState, setTotalState] = useState(data);
    const [validTransaction, setValidTransaction] = useState(false);


    const handleChange = (event) => {
        if(deposit < 0){
            setDeposit(0)
        } else {
            setDeposit(Number(event.target.value));
        }
        setValidTransaction(false);

        if(event.target.value <= 0) return; 
    };

    const handleSubmit = (event) => {
        let newTotal = totalState + deposit;
        setTotalState(newTotal);
        setValidTransaction(true);
        setTransactionsList(...transactionsList, totalState);
        console.log(transactionsList);
        event.preventDefault();

    };




    return(
        <Card
            txtcolor="black"
            header="Deposit"

            body={(
                <>
                    <form onSubmit={handleSubmit}>
                        <div className="row mb-3">
                            <div className="col-auto">
                                <label id="total" className="form-label">BALANCE</label>
                            </div>
                            <div className="col-auto">
                                <label id="total">{totalState}</label>
                            </div>
                        </div>
                        <div className="row mb-3">
                            <label id="total" className="form-label">DEPOSIT AMOUNT</label>
                        </div>
                        <div className="row mb-3">
                            <input id="number-input" type="input"  width="200" onChange={handleChange}/>
                        </div>

                        <div className="row mb-3">
                            <button type="submit" width="200" value="Submit" id="submit-input" disabled={validTransaction}>DEPOSIT</button>
                        </div>


                    </form>


                </>
            )}


        />
    );
};


export { Deposit };
